def hitung_vokal(baris):
    vokal = 'aiueoĀāĪīŪūĔĕÊêÔôÉéÈèĚěOōUūĪīŌōâîêôöûâî'
    jumlah = sum(1 for huruf in baris if huruf.lower() in vokal.lower())
    return jumlah

